import React, { useState } from "react";
import "../App.css";

export default function Apartments() {
    const [apartments, setApartments] = useState([]);
    const [stage, setStage] = useState("idle"); // idle | chooseType | form
    const [selectedType, setSelectedType] = useState("huur");
    const [form, setForm] = useState({
        type: "huur",
        medewerker: "",
        nummer: "",
        beschrijving: "",
        status: "leeg",
        huishouden: ""
    });

    const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

    const startAddFlow = () => {
        setStage("chooseType");
    };

    const confirmType = () => {
        setForm({ ...form, type: selectedType });
        setStage("form");
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const payload = { ...form, type: selectedType };
        const res = await fetch(`${API_URL}/apartments`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
        });
        const data = await res.json();
        if (res.ok) {
            const newApartment = { id: data.id || Date.now(), ...payload };
            setApartments([newApartment, ...apartments]);
            setForm({ type: "huur", medewerker: "", nummer: "", beschrijving: "", status: "leeg", huishouden: "" });
            setSelectedType("huur");
            setStage("idle");
        } else {
            alert(data.message || "Fout bij toevoegen");
        }
    };

    return (
        <div className="apartment-page">
            <div className="apartments-header">
                <h2>Appartementen</h2>
                {stage === "idle" && (
                    <button className="btn-primary add-apartment-btn" onClick={startAddFlow}>Appartement toevoegen</button>
                )}
            </div>

            {stage === "chooseType" && (
                <div className="type-selector glass">
                    <h3>Kies een type</h3>
                    <div className="type-options">
                        <button
                            className={`type-card ${selectedType === "huur" ? "active" : ""}`}
                            onClick={() => setSelectedType("huur")}
                            type="button"
                        >
                            <span className="type-emoji">🔑</span>
                            <span>Huur</span>
                        </button>
                        <button
                            className={`type-card ${selectedType === "verkoop" ? "active" : ""}`}
                            onClick={() => setSelectedType("verkoop")}
                            type="button"
                        >
                            <span className="type-emoji">🏷️</span>
                            <span>Verkoop</span>
                        </button>
                    </div>
                    <div className="type-actions">
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Annuleren</button>
                        <button className="btn-primary" type="button" onClick={confirmType}>Volgende</button>
                    </div>
                </div>
            )}

            {stage === "form" && (
                <form className="apartment-form glass" onSubmit={handleSubmit}>
                    <div className="form-header">
                        <h3>Nieuw appartement ({selectedType})</h3>
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Terug</button>
                    </div>

                    <label>Medewerker</label>
                    <input type="text" name="medewerker" value={form.medewerker} onChange={handleChange} />

                    <label>Nummer</label>
                    <input type="text" name="nummer" value={form.nummer} onChange={handleChange} />

                    <label>Beschrijving</label>
                    <textarea name="beschrijving" value={form.beschrijving} onChange={handleChange}></textarea>

                    <label>Status</label>
                    <select name="status" value={form.status} onChange={handleChange}>
                        <option value="leeg">Leeg</option>
                        <option value="verhuurd">Verhuurd</option>
                    </select>

                    <label>Huishouden</label>
                    <textarea name="huishouden" value={form.huishouden} onChange={handleChange}></textarea>

                    <button className="btn-primary" type="submit">Bewaar</button>
                </form>
            )}

            <div className="apartments-grid">
                {apartments.length === 0 && (
                    <p className="subtle">Nog geen appartementen toegevoegd.</p>
                )}
                {apartments.map((apt) => (
                    <div key={apt.id} className="apartment-card glass">
                        <div className={`badge badge-${apt.type === "huur" ? "huur" : "verkoop"}`}>{apt.type}</div>
                        <h4>Nr. {apt.nummer || "—"}</h4>
                        <p className="muted">{apt.beschrijving || "Geen beschrijving"}</p>
                        <div className="meta">
                            <span>Medewerker: {apt.medewerker || "—"}</span>
                            <span>Status: {apt.status}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
