import React, { useState } from "react";
import "../App.css";
import bgImage from "../assets/venice-mall.jpg";
import logo from "../assets/.png";

export default function Login() {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");

        if (!name || !password) {
            setError("Please fill in both fields!");
            return;
        }

        try {
            const res = await fetch(`${API_URL}/login`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ username: name, password }),
            });

            const data = await res.json();

            if (res.ok) {
                // Optioneel: als backend token terugstuurt, opslaan:
                if (data.token) localStorage.setItem("token", data.token);

                // Sla gebruikersinfo lokaal op (voor demo / redirect)
                localStorage.setItem("user", JSON.stringify({ username: data.username }));

                alert(`Welcome ${data.username}!`);
                // later: redirect naar Dashboard (react-router / useNavigate)
            } else {
                setError(data.message || "Login failed");
            }
        } catch (err) {
            console.error(err);
            setError("Server error. Try again later.");
        }
    };

    return (
        <div className="background" style={{ backgroundImage: `url(${bgImage})` }}>
            <div className="overlay">
                <h1 className="logo">
                    <img src={logo} alt="Logo" className="logo-img" />
                </h1>

                <div className="login-box">
                    <form onSubmit={handleLogin}>
                        <input
                            type="text"
                            placeholder="Name..."
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Password..."
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <button type="submit">Sign In</button>
                        {error && <p className="error">{error}</p>}
                    </form>
                </div>
            </div>
        </div>
    );
}
