import React, { useState } from "react";
import "../App.css";

export default function Apartments() {
    const [apartments, setApartments] = useState([]);
    const [stage, setStage] = useState("idle"); // idle | chooseType | form
    const [selectedType, setSelectedType] = useState("huur");
    const [form, setForm] = useState({
        type: "huur",
        medewerker: "",
        nummer: "",
        beschrijving: "",
        status: "leeg",
        huishouden: ""
    });
    const [detail, setDetail] = useState(null); // selected apartment for modal

    const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

    const startAddFlow = () => {
        setStage("chooseType");
    };

    const confirmType = () => {
        setForm({ ...form, type: selectedType });
        setStage("form");
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const payload = { ...form, type: selectedType };
        const res = await fetch(`${API_URL}/apartments`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
        });
        const data = await res.json();
        if (res.ok) {
            const newApartment = { id: data.id || Date.now(), ...payload };
            setApartments([newApartment, ...apartments]);
            setForm({ type: "huur", medewerker: "", nummer: "", beschrijving: "", status: "leeg", huishouden: "" });
            setSelectedType("huur");
            setStage("idle");
        } else {
            alert(data.message || "Fout bij toevoegen");
        }
    };

    const openDetail = (apt) => setDetail(apt);
    const closeDetail = () => setDetail(null);

    const handleDelete = async (id) => {
        try {
            const res = await fetch(`${API_URL}/apartments/${id}`, { method: "DELETE" });
            if (!res.ok) {
                const data = await res.json().catch(() => ({}));
                alert(data.message || "Delete failed");
                return;
            }
            setApartments(apartments.filter((a) => a.id !== id));
            closeDetail();
        } catch (e) {
            alert("Server error while deleting");
        }
    };

    return (
        <div className="apartment-page">
            <div className="apartments-header">
                <h2>Apartments</h2>
                {stage === "idle" && (
                    <button className="btn-primary add-apartment-btn" onClick={startAddFlow}>Add apartment</button>
                )}
            </div>

            {stage === "chooseType" && (
                <div className="type-selector glass">
                    <h3>Choose a type</h3>
                    <div className="type-options">
                        <button
                            className={`type-card ${selectedType === "huur" ? "active" : ""}`}
                            onClick={() => setSelectedType("huur")}
                            type="button"
                        >
                            <span className="type-emoji">üîë</span>
                            <span>Rent</span>
                        </button>
                        <button
                            className={`type-card ${selectedType === "verkoop" ? "active" : ""}`}
                            onClick={() => setSelectedType("verkoop")}
                            type="button"
                        >
                            <span className="type-emoji">üè∑Ô∏è</span>
                            <span>Sale</span>
                        </button>
                    </div>
                    <div className="type-actions">
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Cancel</button>
                        <button className="btn-primary" type="button" onClick={confirmType}>Next</button>
                    </div>
                </div>
            )}

            {stage === "form" && (
                <form className="apartment-form glass" onSubmit={handleSubmit}>
                    <div className="form-header">
                        <h3>New apartment ({selectedType === "huur" ? "rent" : "sale"})</h3>
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Back</button>
                    </div>

                    <label>Agent</label>
                    <input type="text" name="medewerker" value={form.medewerker} onChange={handleChange} />

                    <label>Number</label>
                    <input type="text" name="nummer" value={form.nummer} onChange={handleChange} />

                    <label>Description</label>
                    <textarea name="beschrijving" value={form.beschrijving} onChange={handleChange}></textarea>

                    <label>Status</label>
                    <select name="status" value={form.status} onChange={handleChange}>
                        <option value="leeg">Empty</option>
                        <option value="verhuurd">Rented</option>
                    </select>

                    <label>Household</label>
                    <textarea name="huishouden" value={form.huishouden} onChange={handleChange}></textarea>

                    <button className="btn-primary" type="submit">Save</button>
                </form>
            )}

            <div className="apartments-grid">
                {apartments.length === 0 && (
                    <p className="subtle">No apartments yet.</p>
                )}
                {apartments.map((apt) => (
                    <button key={apt.id} className="apartment-card glass clickable" onClick={() => openDetail(apt)} type="button">
                        <div className={`badge badge-${apt.type === "huur" ? "huur" : "verkoop"}`}>{apt.type === "huur" ? "rent" : "sale"}</div>
                        <h4>No. {apt.nummer || "‚Äî"}</h4>
                        <p className="muted">{apt.beschrijving || "No description"}</p>
                        <div className="meta">
                            <span>Agent: {apt.medewerker || "‚Äî"}</span>
                            <span>Status: {apt.status}</span>
                        </div>
                    </button>
                ))}
            </div>

            {detail && (
                <div className="modal-backdrop" onClick={closeDetail}>
                    <div className="modal glass" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h3>Apartment details</h3>
                            <button className="icon-btn" onClick={closeDetail} type="button">‚úï</button>
                        </div>
                        <div className="modal-body">
                            <div className="detail-row"><span className="label">Type</span><span>{detail.type === "huur" ? "rent" : "sale"}</span></div>
                            <div className="detail-row"><span className="label">Number</span><span>{detail.nummer || "‚Äî"}</span></div>
                            <div className="detail-row"><span className="label">Agent</span><span>{detail.medewerker || "‚Äî"}</span></div>
                            <div className="detail-row"><span className="label">Status</span><span>{detail.status}</span></div>
                            <div className="detail-row"><span className="label">Description</span><span>{detail.beschrijving || "No description"}</span></div>
                            <div className="detail-row"><span className="label">Household</span><span>{detail.huishouden || "‚Äî"}</span></div>
                        </div>
                        <div className="modal-footer">
                            <button className="btn-secondary" type="button" onClick={closeDetail}>Close</button>
                            <button className="btn-danger" type="button" onClick={() => handleDelete(detail.id)}>Delete apartment</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
