import React, { useState, useEffect } from "react";
import "../App.css";
import defaultPhoto from "../assets/venice-mall.jpg";

export default function Apartments() {
    const [apartments, setApartments] = useState([]);
    const [stage, setStage] = useState("idle");
    const [selectedType, setSelectedType] = useState("rent");
    const [form, setForm] = useState({
        type: "rent",
        agent: "",
        number: "",
        description: "",
        status: "empty",
        household: ""
    });
    const [detail, setDetail] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [editForm, setEditForm] = useState({});

    const API_URL = (process.env.REACT_APP_API_URL || "http://localhost:5000") + "/api";

    // Fetch all apartments from database on load
    useEffect(() => {
        const fetchApartments = async () => {
            try {
                const res = await fetch(`${API_URL}/apartments`);
                const data = await res.json();
                if (res.ok) {
                    setApartments(data);
                } else {
                    console.error(data.message || "Error fetching apartments");
                }
            } catch (err) {
                console.error("Server error:", err);
            }
        };
        fetchApartments();
    }, [API_URL]);

    const startAddFlow = () => {
        setStage("chooseType");
    };

    const confirmType = () => {
        setForm({ ...form, type: selectedType });
        setStage("form");
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const payload = { ...form, type: selectedType };
        const res = await fetch(`${API_URL}/apartments`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
        });
        const data = await res.json();
        if (res.ok) {
            const newApartment = { id: data.id, ...payload };
            setApartments([newApartment, ...apartments]);
            setForm({ type: "rent", agent: "", number: "", description: "", status: "empty", household: "" });
            setSelectedType("rent");
            setStage("idle");
        } else {
            alert(data.message || "Error adding apartment");
        }
    };

    const openDetail = async (apt) => {
        try {
            const res = await fetch(`${API_URL}/apartments/${apt.id}`);
            const data = await res.json();
            if (res.ok) {
                setDetail(data);
                setEditForm(data);
                setIsEditing(false);
            } else {
                console.error(data.message);
                setDetail(apt);
                setEditForm(apt);
                setIsEditing(false);
            }
        } catch (err) {
            console.error("Error fetching apartment detail:", err);
            setDetail(apt);
            setEditForm(apt);
            setIsEditing(false);
        }
    };

    const closeDetail = () => { setDetail(null); setIsEditing(false); };

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setEditForm((prev) => ({ ...prev, [name]: value }));
    };

    const saveEdits = async () => {
        try {
            const payload = {
                type: editForm.type || "",
                agent: editForm.agent || "",
                number: editForm.number || "",
                description: editForm.description || "",
                status: editForm.status || "empty",
                household: editForm.household || "",
            };

            console.log("Saving apartment ID:", detail.id, editForm);

            const res = await fetch(`${API_URL}/apartments/${detail.id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload),
            });
            const data = await res.json();
            if (res.ok) {
                setDetail(data);
                setApartments((prev) => prev.map((a) => (a.id === data.id ? data : a)));
                setIsEditing(false);
            } else {
                alert(data.message || "Update failed");
            }
        } catch (e) {
            alert("Server error while saving");
        }
    };

    const handleDelete = async (id) => {
        try {
            const res = await fetch(`${API_URL}/apartments/${id}`, { method: "DELETE" });
            if (!res.ok) {
                const data = await res.json().catch(() => ({}));
                alert(data.message || "Delete failed");
                return;
            }
            setApartments(apartments.filter((a) => a.id !== id));
            closeDetail();
        } catch (e) {
            alert("Server error while deleting");
        }
    };

    return (
        <div className="apartment-page">
            <div className="apartments-header">
                <h2>Apartments</h2>
                {stage === "idle" && (
                    <button className="btn-primary add-apartment-btn" onClick={startAddFlow}>Add apartment</button>
                )}
            </div>

            {stage === "chooseType" && (
                <div className="type-selector glass">
                    <h3>Choose a type</h3>
                    <div className="type-options">
                        <button
                            className={`type-card ${selectedType === "rent" ? "active" : ""}`}
                            onClick={() => setSelectedType("rent")}
                            type="button"
                        >
                            <span className="type-emoji">üîë</span>
                            <span>Rent</span>
                        </button>
                        <button
                            className={`type-card ${selectedType === "sale" ? "active" : ""}`}
                            onClick={() => setSelectedType("sale")}
                            type="button"
                        >
                            <span className="type-emoji">üè∑Ô∏è</span>
                            <span>Sale</span>
                        </button>
                    </div>
                    <div className="type-actions">
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Cancel</button>
                        <button className="btn-primary" type="button" onClick={confirmType}>Next</button>
                    </div>
                </div>
            )}

            {stage === "form" && (
                <form className="apartment-form glass" onSubmit={handleSubmit}>
                    <div className="form-header">
                        <h3>New apartment ({selectedType})</h3>
                        <button className="btn-secondary" type="button" onClick={() => setStage("idle")}>Back</button>
                    </div>

                    <label>Agent</label>
                    <input type="text" name="agent" value={form.agent} onChange={handleChange} />

                    <label>Number</label>
                    <input type="text" name="number" value={form.number} onChange={handleChange} />

                    <label>Description</label>
                    <textarea name="description" value={form.description} onChange={handleChange}></textarea>

                    <label>Status</label>
                    <select name="status" value={form.status} onChange={handleChange}>
                        <option value="empty">Empty</option>
                        <option value="rented">Rented</option>
                    </select>

                    <label>Household</label>
                    <textarea name="household" value={form.household} onChange={handleChange}></textarea>

                    <button className="btn-primary" type="submit">Save</button>
                </form>
            )}

            <div className="apartments-grid">
                {apartments.length === 0 && (
                    <p className="subtle">No apartments yet.</p>
                )}
                {apartments.map((apt) => {
                    const isEmpty = (apt.status || "").toLowerCase() === "empty";
                    return (
                        <button
                            key={apt.id}
                            className="apartment-card icon-card clickable"
                            onClick={() => openDetail(apt)}
                            type="button"
                            title={`Apartment ${apt.number || ""}`}
                        >
                            <div className="icon">üè†</div>
                            <div className="apartment-info">
                                <span className="apartment-number">
                                    {apt.number || "‚Äî"}
                                </span>
                                <span
                                    className={`status-dot ${isEmpty ? "status-green" : "status-red"}`}
                                />
                            </div>
                        </button>
                    );
                })}
            </div>

            {detail && (
                <div className="modal-backdrop" onClick={closeDetail}>
                    <div className="modal glass" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h3>Apartment details</h3>
                            <button className="icon-btn" onClick={closeDetail} type="button">‚úï</button>
                        </div>
                        <div className="modal-body">
                            <div className="detail-row">
                                <span className="label">Type</span>
                                {isEditing ? (
                                    <select name="type" value={editForm.type || "rent"} onChange={handleEditChange}>
                                        <option value="rent">Rent</option>
                                        <option value="sale">Sale</option>
                                    </select>
                                ) : (
                                    <span>{detail.type === "rent" ? "Rent" : "Sale"}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Number</span>
                                {isEditing ? (
                                    <input name="number" type="text" value={editForm.number || ""} onChange={handleEditChange} />
                                ) : (
                                    <span>{detail.number || "‚Äî"}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Agent</span>
                                {isEditing ? (
                                    <input name="agent" type="text" value={editForm.agent || ""} onChange={handleEditChange} />
                                ) : (
                                    <span>{detail.agent || "‚Äî"}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Status</span>
                                {isEditing ? (
                                    <select name="status" value={editForm.status || "empty"} onChange={handleEditChange}>
                                        <option value="empty">Empty</option>
                                        <option value="rented">Rented</option>
                                    </select>
                                ) : (
                                    <span>{detail.status}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Description</span>
                                {isEditing ? (
                                    <textarea name="description" value={editForm.description || ""} onChange={handleEditChange}></textarea>
                                ) : (
                                    <span>{detail.description || "No description"}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Photo URL</span>
                                {isEditing ? (
                                    <input name="photo" type="text" value={editForm.photo || editForm.image || ""} onChange={handleEditChange} />
                                ) : (
                                    <span>{detail.photo || detail.image || "‚Äî"}</span>
                                )}
                            </div>
                            <div className="detail-row">
                                <span className="label">Household</span>
                                <span style={{ display: "flex", alignItems: "center", gap: 8 }}>
                                    {isEditing ? (
                                        <textarea name="household" value={editForm.household || ""} onChange={handleEditChange}></textarea>
                                    ) : (
                                        <span>{detail.household || "‚Äî"}</span>
                                    )}
                                    {!isEditing && (
                                        <button
                                            className="icon-btn"
                                            type="button"
                                            title={String(detail.household || "").trim().startsWith("‚úÖ") ? "Mark as not done" : "Mark as done"}
                                            onClick={async () => {
                                                try {
                                                    const wantDone = !String(detail.household || "").trim().startsWith("‚úÖ");
                                                    const res = await fetch(`${API_URL}/apartments/${detail.id}/household-done`, {
                                                        method: "PATCH",
                                                        headers: { "Content-Type": "application/json" },
                                                        body: JSON.stringify({ done: wantDone })
                                                    });
                                                    const data = await res.json();
                                                    if (res.ok) {
                                                        setDetail(data);
                                                        setApartments((prev) => prev.map((a) => (a.id === data.id ? data : a)));
                                                    }
                                                } catch (e) {
                                                    // ignore
                                                }
                                            }}
                                        >
                                            {String(detail.household || "").trim().startsWith("‚úÖ") ? "‚òë" : "‚òê"}
                                        </button>
                                    )}
                                </span>
                            </div>
                        </div>
                        <div className="modal-footer">
                            {!isEditing ? (
                                <>
                                    <button className="btn-secondary" type="button" onClick={() => { setIsEditing(true); setEditForm(detail); }}>Edit</button>
                                    <button className="btn-danger" type="button" onClick={() => handleDelete(detail.id)}>Delete apartment</button>
                                </>
                            ) : (
                                <>
                                    <button className="btn-secondary" type="button" onClick={() => { setIsEditing(false); setEditForm(detail); }}>Cancel</button>
                                    <button className="btn-primary" type="button" onClick={saveEdits}>Save</button>
                                </>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
